import React from 'react';
import { Radio, RadioGroup } from 'react-radio-group';

const ButtonOptions = () => {
  const handleOnChange = (value) => {
    // Perform any logic or actions based on the selected value
    console.log('Selected fruit:', value);
  };

  return (
    <RadioGroup name="fruits" onChange={handleOnChange}>
      <div className="radio-button-background">
        <Radio value="Apple" className="radio-button" />Apple
      </div>
      <div className="radio-button-background">
        <Radio value="Orange" className="radio-button" />Orange
      </div>
      <div className="radio-button-background">
        <Radio value="Banana" className="radio-button" />Banana
      </div>
    </RadioGroup>
  );
};

export default ButtonOptions;
