  };

  return (
    <>
      <div className="page-container">
        <TopBar openSideBar={toggleSideBar} />
        <Backdrop back sidebar={sidebar} closeSideBar={toggleSideBar} />
        <SideBar sidebar={sidebar} toggleSideBar={toggleSideBar} />
        <div className="course-alphabet-column">
          <div className="row">
            {sortedLetters.map((letter) => (
              <div className="col-md-4" key={letter}>
                <div className="category-container">
                  <h2 className="category-title">{letter}</h2>
                  <ul className="course-list">
                    {[...groupedCourses[letter]].map((deptName, index) => (
                      <li
                        className="course-item"
                        key={index}
                        onClick={() => listOnClick(deptName)}
                      >
                        {deptName}
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="bottom-bar-wrapper">
        <BottomBar className="bottom-bar"></BottomBar>
      </div>
    </>
  );
};

export default CourseInfo;
