import TopBar from "../components/TopBar";
import Backdrop from "../components/BackDrop";
import SideBar from "../components/SideBar";
import BottomBar from "../components/BottomBar";
import "./Courses.css";
import CourseList from "../components/CourseList";
import { fetchDataDep } from "../components/DataFetcherDep";

const Courses = () => {
  const fetchDataAsync = async () => {
    if (selectedOption1) {
      console.log(selectedOption1);
      const jsonData = await fetchData(selectedOption1);
      setData(jsonData);
    }
  };

  fetchDataAsync();

  const [sidebar, setSideBar] = useState(false);

  const toggleSideBar = () => {
    setSideBar((prevState) => !prevState);
  };

  return (
    <>
      <div className="page-container">
        <TopBar openSideBar={toggleSideBar} />
        <Backdrop back sidebar={sidebar} closeSideBar={toggleSideBar} />
        <SideBar sidebar={sidebar} toggleSideBar={toggleSideBar} />
      </div>
      <div classNameName="bottom-bar-wrapper">
        <BottomBar classNameName="bottom-bar"></BottomBar>
      </div>
    </>
  );
};

export default Courses;
