import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import SelectOption from './SelectOption';
import SelectOptionClasses from './SelectOptionClasses';
import TopBar from './TopBar';
import { fetchData } from './DataFetcher';
import TeacherCardGrid from './TeacherCardGrid';
import './Home.css';

const Home = () => {

  const [selectedOption1, setSelectedOption1] = useState('');
  const [selectedOption2, setSelectedOption2] = useState('');
  const [data, setData] = useState([]);
  const [teacherData, setTeacherData] = useState([]);

  const handleOptionChange1 = (event) => {
    const selectedValue = event.target.value;
    setSelectedOption1(selectedValue);
    setSelectedOption2('');
    setTeacherData([]);
  };

  const handleOptionChange2 = (event) => {

    const selectedValue = event.target.value;
    setSelectedOption2(selectedValue);

    const filteredTeacherData = data.filter((item) => {
      const optionValue = `${item.CRSSUBJCD} ${item.CRSNBR} - ${item.CRSTITLE}`;
      return optionValue === selectedValue;
    });

    setTeacherData(filteredTeacherData);

  };

  useEffect(() => {
    setSelectedOption1('');
  }, []);

  useEffect(() => {
    const fetchDataAsync = async () => {
      if (selectedOption1) {
        const jsonData = await fetchData(selectedOption1);
        setData(jsonData);
      }
    };

    fetchDataAsync();
  }, [selectedOption1]);

  const termNames = [
    { label: 'Fall 2020', value: 'Fall2020' },
    { label: 'Fall 2021', value: 'Fall2021' },
    { label: 'Fall 2022', value: 'Fall2022' },
    { label: 'Spring 2021', value: 'Spring2021' },
    { label: 'Spring 2022', value: 'Spring2022' },
    { label: 'Summer 2021', value: 'Summer2021' },
    { label: 'Summer 2022', value: 'Summer2022' },
  ];

  const classNames = [];

  const isOption2Disabled = selectedOption1 === 'Select Term' || selectedOption1 === '';

  return (
    <>
      <TopBar />
      <Container className="styled-container">
      <h5>Search for a class below!</h5>
        <SelectOption
          options={termNames}
          value={selectedOption1}
          onChange={handleOptionChange1}
          disabled={false}
          defaultText="Select Term"
        />
        <SelectOptionClasses
          options={data}
          value={selectedOption2}
          onChange={handleOptionChange2}
          disabled={isOption2Disabled}
          defaultText="Select Class"
        />
      </Container>
      <TeacherCardGrid teachers={teacherData}/>
    </>
  );
};

export default Home;
