import React from "react";
import "./CourseList.css"; // Import the CSS file for styling

const CourseList = ({ courses }) => {
  // Create an object to store courses grouped by the first letter of department names
  const groupedCourses = {};

  // Iterate over the courses and group them by the first letter of the department name
  Object.values(courses).forEach((categoryCourses) => {
    categoryCourses.forEach((course) => {
      const firstLetter = course.DEPTNAME.charAt(0).toUpperCase();
      if (!groupedCourses[firstLetter]) {
        groupedCourses[firstLetter] = [];
      }
      groupedCourses[firstLetter].push(course);
    });
  });

  // Sort the keys (letters) alphabetically
  const sortedLetters = Object.keys(groupedCourses).sort();

  return (
    <div className="course-alphabet-column">
      <div className="row">
        {sortedLetters.map((letter) => (
          <div className="col-md-4" key={letter}>
            <div className="category-container">
              <h2 className="category-title">{letter}</h2>
              <ul className="course-list">
                {groupedCourses[letter].map((course, index) => (
                  <li className="course-item" key={index}>
                    {course.DEPTNAME}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseList;
