import React from 'react';
import { FormSelect as Select, Container } from 'react-bootstrap';
import './SelectOption.css'; // Import the CSS file

const SelectOptionClasses = ({
  options,
  value,
  onChange,
  disabled,
  defaultText,
  displayInstructor,
  teacher,
}) => {
  const filteredOptions = options.filter((option) => option.id !== teacher.id);

  return (
    <Container className="select-option-container">
      <Select className="select-option" disabled={disabled} value={value} onChange={onChange}>
        <option value="">{defaultText}</option>
        {filteredOptions.map((option, index) => (
          <option key={index} value={option.value}>
            {!displayInstructor
              ? `${option.CRSSUBJCD} ${option.CRSNBR} - ${option.CRSTITLE}`
              : option.PrimaryInstructor}
          </option>
        ))}
      </Select>
    </Container>
  );
};

export default SelectOptionClasses;
