import React, { useState } from "react";
import TopBar from "../components/TopBar";
import BottomBar from "../components/BottomBar";
import SideBar from "../components/SideBar";
import Backdrop from "../components/BackDrop";
import { useLocation } from "react-router-dom";
import "./CourseInfo.css";
import { useNavigate } from "react-router-dom";

const CourseInfo = () => {
  const location = useLocation();
  const { deptData } = location.state || {};
  const { teacherData, setTeacherData } = useState([]);

  const handleOptionChange1 = (event) => {};

  const [sidebar, setSideBar] = useState(false);

  const handleOptionChange2 = (option) => {
    const selectedValue = option;

    const filteredTeacherData = deptData.filter((item) => {
      const optionValue = `${item.CRSTITLE}`;
      return optionValue === selectedValue;
    });

    console.log(filteredTeacherData);
    setTeacherData(filteredTeacherData);
  };

  const toggleSideBar = () => {
    setSideBar((prevState) => !prevState);
  };

  const deptName = deptData[0].DEPTNAME;

  const navigate = useNavigate();
  const handleClick = () => {
    // navigate(`/InstructorData/${teacher.PrimaryInstructor}`, {
    //   state: { teacher, teachers },
    // });
  };

  return (
    <>
      <div className="page-container">
        <TopBar openSideBar={toggleSideBar} />
        <Backdrop back sidebar={sidebar} closeSideBar={toggleSideBar} />
        <SideBar sidebar={sidebar} toggleSideBar={toggleSideBar} />
        <div className="course-column col-md-4">
          <div className="category-container">
            <h2 className="category-title">{deptName}</h2>
            <ul className="course-list">
              {deptData.map((item, index) => (
                <li
                  className="course-item"
                  key={index}
                  onClick={handleOptionChange2}
                >
                  {item.CRSTITLE}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <div className="bottom-bar-wrapper">
        <BottomBar className="bottom-bar"></BottomBar>
      </div>
    </>
  );
};

export default CourseInfo;
