import React from 'react';
import { Container } from 'react-bootstrap';
import "bootstrap/dist/css/bootstrap.min.css";

const SelectOption = ({ options, value, onChange, disabled, defaultText}) => {
  return (
    <Container>
      <select className={`form-select {disabled}`} value={value} onChange={onChange} disabled={disabled}>
        <option selected>{defaultText}</option>
        {options.map((option) => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
    </Container>
  );
};

export default SelectOption;
