import React from 'react';
import { Card } from 'react-bootstrap';
import logo from './user-logo.png'; // Import the logo image
import './TeacherCard.css'; // Import the CSS file
import { Link } from 'react-router-dom';

const TeacherCard = ({name, dep, teacher}) => {

    return (
            <Card className="teacher-card"> 
                <Link to={{ pathname: `/TeacherView/${teacher.PrimaryInstructor}`, state: { teacher } }}>
                <img src={logo} className="card-img-top" alt="..." />
                <div className="card-body">
                    <h5 className="card-title">{name}</h5>
                    <p className="card-text">Department: {dep}</p>
                </div>
                </Link>
            </Card>

    );
}

export default TeacherCard ;
