import "bootstrap/dist/css/bootstrap.min.css";
import React from 'react';
import { FormSelect as Select, Container} from 'react-bootstrap';

const SelectOption = ({ options, value, onChange, disabled, defaultText}) => {
  return (
    <>
    <Container className="align-items-left justify-content-center">
    <Select value={value} onChange={onChange}>
    <option selected>Open this select menu</option>
      {options.map((option) => (
        <option key={option.value} value={option.value}>
          {option.label}
        </option>
      ))}
    </Select>
    </Container>
    </>
  );
};

export default SelectOption;
