import React from "react";
import "./CourseList.css"; // Import the CSS file for styling

const CourseList = ({ courses }) => {
  // Create a Set to store unique courses
  const uniqueCourses = new Set();

  // Iterate over the courses and add unique courses to the Set
  Object.values(courses).forEach((categoryCourses) => {
    categoryCourses.forEach((course) => {
      uniqueCourses.add(course.DEPTNAME);
    });
  });

  // Convert the Set to an array and sort it alphabetically
  const sortedCourses = Array.from(uniqueCourses).sort();

  return (
    <div className="course-alphabet-column">
      <div className="row">
        {sortedCourses.map((course, index) => (
          <div className="col-md-4" key={index}>
            <div className="category-container">
              <h2 className="category-title">{course.charAt(0)}</h2>
              <ul className="course-list">{course}</ul>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CourseList;
