import React, { useState, useEffect, useRef } from "react";
import "./Dropdown.css";

const DropDownProfessor = ({ selected, setDropdown, options, onChange }) => {
  const [isActive, setIsActive] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [isOptionSelected, setIsOptionSelected] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsActive(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleOptionClick = (option) => {
    setDropdown(option);
    setIsOptionSelected(true);
    setIsActive(false);
    onChange(option.PrimaryInstructor);
  };

  const handleDropdownClick = () => {
    setIsOptionSelected(false);
    setIsActive(!isActive);
  };

  return (
    <div className="dropdown" ref={dropdownRef}>
      <div className="dropdown-btn" onClick={handleDropdownClick}>
        {selected ? selected : "Select Professor"}
        {!isOptionSelected && <i className="ri-arrow-down-s-fill"></i>}
      </div>
      {isActive && (
        <div className="dropdown-content">
          <div className="dropdown-options">
            {options.map((option, index) => (
              <div
                key={index}
                onClick={() => handleOptionClick(option)}
                className="dropdown-item"
              >
                <span>{option.PrimaryInstructor}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default DropDownProfessor;
