import React, { useState } from "react";
import TopBar from "../components/TopBar";
import BottomBar from "../components/BottomBar";
import SideBar from "../components/SideBar";
import Backdrop from "../components/BackDrop";
import { useLocation } from "react-router-dom";
import "./CourseInfo.css";

const CourseInfo = () => {
  const location = useLocation();
  const { deptData, deptName } = location.state || {};
  const [selectedOption1, setSelectedOption1] = useState("");

  const handleOptionChange1 = (event) => {};

  const [sidebar, setSideBar] = useState(false);

  const toggleSideBar = () => {
    setSideBar((prevState) => !prevState);
  };

  return (
    <>
      <div className="page-container">
        <TopBar openSideBar={toggleSideBar} />
        <Backdrop back sidebar={sidebar} closeSideBar={toggleSideBar} />
        <SideBar sidebar={sidebar} toggleSideBar={toggleSideBar} />
        <div className="course-info-column">
          <ul className="dept-data-list">
            {deptData.map((item, index) => (
              <li className="dept-data-item" key={index}>
                {item.CRSTITLE}
              </li>
            ))}
          </ul>
        </div>
      </div>

      <div className="bottom-bar-wrapper">
        <BottomBar className="bottom-bar"></BottomBar>
      </div>
    </>
  );
};

export default CourseInfo;
