import React from 'react';
import { FormSelect as Select, Container } from 'react-bootstrap';
import './SelectOption.css'; // Import the CSS file

const SelectOption = ({ options, value, onChange, disabled, defaultText }) => {
  return (
    <Container className="select-option-container">
      <Select className="select-option" disabled={disabled} value={value} onChange={onChange}>
        <option value="">{defaultText}</option>
        {options.map((option) => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </Select>
    </Container>
  );
};

export default SelectOption;
