import React, { useState } from "react";
import TopBar from "../components/TopBar";
import Backdrop from "../components/BackDrop";
import SideBar from "../components/SideBar";
import BottomBar from "../components/BottomBar";
import "./About.css";
import { Container } from "react-bootstrap";

const About = () => {
  const [sidebar, setSideBar] = useState(false);

  const toggleSideBar = () => {
    setSideBar((prevState) => !prevState);
  };

  return (
    <>
      <div className="page-container">
        <section class="section about-section gray-bg" id="about">
          <div class="container">
            <div class="row align-items-center justify-content-around flex-row-reverse">
              <div class="col-lg-6">
                <div class="about-text">
                  <h3 class="dark-color">Do some awsome stuff with me.</h3>
                  <h4 class="theme-color">
                    UI / UX Designer &amp; Web Developer
                  </h4>
                  <p>
                    I design and develop services for customers of all sizes,
                    specializing in creating stylish, modern websites, web
                    services and online stores. My passion is to design digital
                    user experiences through the bold interface and meaningful
                    interactions.
                  </p>
                  <p>
                    I design and develop services for customers of all sizes,
                    specializing in creating stylish, modern websites, web
                    services and online stores.
                  </p>
                  <div class="btn-bar">
                    <a class="px-btn theme" href="#">
                      Linkedin
                    </a>
                    <a class="px-btn theme-t" href="#">
                      GitHub
                    </a>
                  </div>
                </div>
              </div>
              <div class="col-lg-5 text-center">
                <div class="about-img">
                  <img src="https://bootdey.com/img/Content/avatar/avatar1.png" />
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    </>
  );
};

export default About;
