import React, { useEffect, useState } from 'react';
import TopBar from '../components/TopBar.js';
import { Container } from 'react-bootstrap';
import './InstructorData.css';
import { useLocation } from 'react-router-dom';
import Graph from '../components/Graph.js';
import SelectOptionClasses from '../components/SelectOptionClasses.js';
import { Link, useNavigate } from 'react-router-dom';
import BottomBar from '../components/BottomBar.js';
import ButtonOptions from '../components/ButtonOptions.js';


const InstructorData = () => {

  const location = useLocation();
  const { teacher, teachers } = location.state || {};

  const [data, setData] = useState([]);
  const [selectedOption1, setSelectedOption1] = useState('');

  useEffect(() =>{
    setData(teachers);
  }, []);


  const navigate = useNavigate();

  const handleOptionChange1 = (event) => {
    const selectedValue = event.target.value;
    setSelectedOption1(selectedValue);

    const selectedTeacher = teachers.find((teacher) => teacher.PrimaryInstructor === selectedValue);

    if (selectedTeacher) {
      navigate(`/InstructorData/${selectedTeacher.PrimaryInstructor}`, {
        state: { teacher: selectedTeacher, teachers },
      });
    }
  };

  return (
    <>
      <TopBar />
      <Container className="container-style">
        {teacher && <h2 className="container-text">{teacher.PrimaryInstructor}</h2>}
        {teacher && <h4 className="container-text">{teacher.CRSSUBJCD + " " + teacher.CRSNBR + " - " + teacher.CRSTITLE}</h4>}
        <SelectOptionClasses options={data} value={selectedOption1} onChange={handleOptionChange1} disabled={false} defaultText="Select Other Professors" displayInstructor={true}/>
      </Container> 
      <ButtonOptions text1={"Bar Chart"} text2={"Pie Graph"}></ButtonOptions>
      {teacher && <Graph chartData={teacher}></Graph>}
      <BottomBar></BottomBar>
    </>
  );
};

export default InstructorData;
