import React, { useState } from "react";
import TopBar from "../components/TopBar";
import Backdrop from "../components/BackDrop";
import SideBar from "../components/SideBar";
import BottomBar from "../components/BottomBar";
import "./About.css";
import { Container } from "react-bootstrap";
import logo from "./about-logo.JPG";

const About = () => {
  const [sidebar, setSideBar] = useState(false);

  const toggleSideBar = () => {
    setSideBar((prevState) => !prevState);
  };

  return (
    <>
      <div className="page-container">
        <TopBar openSideBar={toggleSideBar} />
        <Backdrop back sidebar={sidebar} closeSideBar={toggleSideBar} />
        <SideBar sidebar={sidebar} />
        <section className="section about-section gray-bg" id="about">
          <div className="container">
            <div className="row align-items-center justify-content-around flex-row-reverse">
              <div className="col-lg-6">
                <div className="about-text">
                  <h3 className="dark-color">Daniyal Siddiqui</h3>
                  <h4 className="theme-color">Aspiring Software Engineer</h4>
                  <p>
                    I'm a Computer Science student at the University of Illinois
                    - Chicago
                  </p>
                  <p>
                    I design and develop services for customers of all sizes,
                    specializing in creating stylish, modern websites, web
                    services and online stores.
                  </p>
                  <div className="btn-bar">
                    <a className="px-btn theme" href="#">
                      Linkedin
                    </a>
                    <a className="px-btn theme-t" href="#">
                      GitHub
                    </a>
                  </div>
                </div>
              </div>
              <div className="about-img-container col-lg-4 text-center">
                <div className="grow">
                  <div className="about-img">
                    <img src={logo} />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
      <div classNameName="bottom-bar-wrapper">
        <BottomBar classNameName="bottom-bar"></BottomBar>
      </div>
    </>
  );
};

export default About;
